import rpy2.robjects as robjects
robjects.r("source('GC_plot.R')")
import os
filepath ='C://Users//41518//Desktop'
seq = ''
size =30
type1 = "GC"
aa = robjects.r['GC_plot'](seq,size,type1,filepath)

A=aa.rx(1,1)[0]
T=aa.rx(1,2)[0]
G=aa.rx(1,3)[0]
C=aa.rx(1,4)[0]
total=A+T+G+C
print(A,T,G,C,total)
A_percent="%.2f%%" % (round(A/total*100,2))
